import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

// Utility function to convert UTC to local time
const formatLocalTime = (utcDateString) => {
    if (!utcDateString) return 'Date not available';
    
    try {
        const date = new Date(utcDateString);
        return date.toLocaleString('en-US', {
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            hour12: true,
            timeZone: 'UTC'  // Add this to display UTC time as-is

        });
    } catch (error) {
        console.error('Error formatting date:', error);
        return 'Invalid date';
    }
};

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    fetchProfileData();
  }, []);

  const fetchProfileData = async () => {
    try {
      const response = await axios.get('/api/profile', {
        withCredentials: true
      });
      setProfileData(response.data);
    } catch (error) {
      console.error('Error fetching profile data:', error);
    } finally {
      setLoading(false);
    }
  };

  const deleteSearch = async (searchId) => {
    if (window.confirm('Delete this search history?')) {
      try {
        await axios.delete(`/api/delete-search/${searchId}`, {
          withCredentials: true
        });
        fetchProfileData(); // Refresh data
      } catch (error) {
        console.error('Error deleting search:', error);
      }
    }
  };

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="d-flex justify-content-center">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-4">
          <div className="card">
            <div className="card-body text-center">
              <div className="mb-3">
                <i 
                  className="bi bi-person-circle" 
                  style={{ fontSize: '3rem', color: '#0d6efd' }}
                ></i>
              </div>
              <h5 className="card-title">{user?.name}</h5>
              <p className="card-text text-muted">{user?.email}</p>
              <p className="card-text">
                <small className="text-muted">
                  Member since: {formatLocalTime(profileData?.user.created_at)}
                </small>
              </p>
            </div>
          </div>
        </div>
        
        <div className="col-md-8">
          <div className="card">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="mb-0">Search History</h5>
              <span className="badge bg-primary">
                {profileData?.search_history.length || 0} searches
              </span>
            </div>
            
            <div className="card-body">
              {profileData?.search_history.length > 0 ? (
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th>Type</th>
                        <th>Brand</th>
                        <th>Product</th>
                        <th>OEM</th>
                        <th>ASIN</th>
                        <th>Website</th>
                        <th>Date</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {profileData.search_history.map((search) => (
                        <tr key={search.id}>
                          <td>
                            <span 
                              className={`badge ${
                                search.search_type === 'bulk' ? 'bg-warning' : 'bg-info'
                              }`}
                            >
                              {search.search_type}
                            </span>
                          </td>
                          <td>{search.brand}</td>
                          <td>{search.product}</td>
                          <td>{search.oem_number || 'N/A'}</td>
                          <td>{search.asin_number || 'N/A'}</td>
                          <td>{search.website || 'All'}</td>
                          <td>
                            {formatLocalTime(search.created_at)}
                          </td>
                          <td>
                            <button
                              className="btn btn-sm btn-outline-danger"
                              onClick={() => deleteSearch(search.id)}
                            >
                              <i className="bi bi-trash"></i>
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="text-center py-4">
                  <i 
                    className="bi bi-clock-history" 
                    style={{ fontSize: '3rem', color: '#6c757d' }}
                  ></i>
                  <p className="text-muted mt-3">No search history found.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
