import React, { useState, useCallback } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Dashboard.css';
import '../styles/Table.css';

const Dashboard = () => {
  const [formData, setFormData] = useState({
    brand: '',
    product: '',
    oem_number: '',
    asin_number: '',
    website: ''
  });
  const [results, setResults] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [bulkLoading, setBulkLoading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const { user } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleScrape = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResults([]);

    try {
      const response = await axios.post('/api/scrape', formData, {
        withCredentials: true
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setResults(response.data.data);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred during scraping');
    } finally {
      setLoading(false);
    }
  };

  const handleDrag = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      handleFileSelection(file);
    }
  }, []);

  const handleFileInput = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFileSelection(e.target.files[0]);
    }
  };

  const handleFileSelection = (file) => {
    const validTypes = ['.csv', '.xlsx', '.xls'];
    const fileExtension = '.' + file.name.split('.').pop().toLowerCase();

    if (!validTypes.includes(fileExtension)) {
      setError('Please upload a CSV or Excel file (.csv, .xlsx, .xls)');
      return;
    }

    if (file.size > 10 * 1024 * 1024) {
      setError('File size should be less than 10MB');
      return;
    }

    setSelectedFile(file);
    setError('');
  };

const handleBulkUpload = async () => {
  if (!selectedFile) {
    setError('Please select a file first');
    return;
  }

  setBulkLoading(true);
  setError('');
  setResults([]);

  try {
    // Read the file and convert to JSON format that backend expects
    const reader = new FileReader();

    reader.onload = async (e) => {
      try {
        const data = e.target.result;
        let jsonData;

        if (selectedFile.name.endsWith('.csv')) {
          // Parse CSV
          const lines = data.split('\n');
          const headers = lines[0].split(',').map(h => h.trim());
          jsonData = lines.slice(1).map(line => {
            const values = line.split(',').map(v => v.trim());
            const row = {};
            headers.forEach((header, index) => {
              row[header] = values[index] || '';
            });
            return row;
          });
        } else {
          // For Excel files, we'll send as FormData since backend handles it
          const formData = new FormData();
          formData.append('file', selectedFile);

          const response = await axios.post('/api/scrape', formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
            },
            withCredentials: true
          });

          if (response.data.error) {
            setError(response.data.error);
          } else {
            setResults(response.data.data);
            setSelectedFile(null);
          }
          setBulkLoading(false);
          return;
        }

        // Process each row individually for CSV
        const allResults = [];
        for (const row of jsonData) {
          if (!row.Brand || !row.Product) continue;

          const scrapeData = {
            brand: row.Brand,
            product: row.Product,
            oem_number: row['OEM Number'] || '',
            asin_number: row['ASIN Number'] || '',
            website: row['Website Name'] || ''
          };

          const response = await axios.post('/api/scrape', scrapeData, {
            withCredentials: true
          });

          if (response.data.data) {
            allResults.push(...response.data.data);
          }

          // Add delay between requests to avoid rate limiting
          await new Promise(resolve => setTimeout(resolve, 1000));
        }

        setResults(allResults);
        setSelectedFile(null);

      } catch (error) {
        setError(error.response?.data?.error || 'An error occurred during bulk upload');
      } finally {
        setBulkLoading(false);
      }
    };

    if (selectedFile.name.endsWith('.csv')) {
      reader.readAsText(selectedFile);
    } else {
      // For Excel files, use FormData approach
      const formData = new FormData();
      formData.append('file', selectedFile);

      const response = await axios.post('/api/scrape', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        withCredentials: true
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setResults(response.data.data);
        setSelectedFile(null);
      }
      setBulkLoading(false);
    }

  } catch (error) {
    setError(error.response?.data?.error || 'An error occurred during bulk upload');
    setBulkLoading(false);
  }
};

